import './App.css';
import React, { useState } from "react";

function App() {

  const [jobTitle, setJobTitle] = useState("");
  const [location, setLocation] = useState("");
  const [results, setResults] = useState([]);
  const [savedJobs, setSavedJobs] = useState([]);

  const handleSearch = async () => {
    const url = "https://jooble.org/api/";
    const key = "11e139a9-52d1-43f1-a99b-514e61ab8a63"; 
    const params = {
      keywords: jobTitle,
      location: location,
    };

    try {
      const response = await fetch(`${url}${key}`, {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify(params),
      });

      if (!response.ok) {
        throw new Error(`Error: ${response.status}`); 
      }

      const data = await response.json(); 
      setResults(data.jobs || []); 
    } catch (error) {
      console.error("Failed to find job results:", error);
    }
  };

  const handleSaveJob = (job) => {
    setSavedJobs((prevJobs) => [...prevJobs, job]);
  };

  return (
    <div className="App">
      <header>
        <h1>Find you dream job!</h1>
      </header>
      <div className="search-container">
        <input
          type="text"
          placeholder="Job Title (e.g., Product Manager)"
          value={jobTitle}
          onChange={(e) => setJobTitle(e.target.value)}
        />
        <input
          type="text"
          placeholder="Location (e.g., Seattle)"
          value={location}
          onChange={(e) => setLocation(e.target.value)}
        />
        <button onClick={handleSearch}>Search</button>
      </div>
      <div className="results-container">
        <h2>Search Results</h2>
        <h3>{job.title}</h3>
            <p>{job.company}</p>
            <p>{job.location}</p>
        </div>

    </div>
  );
}

export default App;
